jQuery.getJSON('https://personal-manik.firebaseio.com/socials.json', function(res){
    jQuery.each(res,function(k,v){
        jQuery('#socialLinks').append(`<a href="${v.url}" target="_blank" class="social-link" data-title="${v.name}">
            <i class="fa ${v.icon}"></i>
        </a>`);
    });
});
jQuery.getJSON('https://personal-manik.firebaseio.com/about.json', function(res){
    jQuery('#home').find('.thumb').parallax({imageSrc:res.home_bg,zIndex:-110});
    jQuery('#about').find('.thumb').parallax({imageSrc:res.background});
    jQuery('#home').find('.content').parallax({imageSrc:res.home_right_bg,speed:0.7});

    jQuery('#dataIntro').html(res.intro);
    jQuery('#dataThanks').html(res.thanks);
    jQuery('#dataPhoto').attr("src",res.photo)
    if (res.cv.public) {
        jQuery('#cv-link').html('<a target="_blank" :href="'+res.cv.link+'" class="cv-button">Download Resume</a>');
    }
    jQuery.each(res.info,function(k,v){
        jQuery('#dataGeneral').find('.list-group').append(`
        <li class="list-group-item d-flex">
            <span title="${v.key}" class="mr-auto">
                <i class="fa ${v.icon} mr-2"></i>
                <span class="d-none d-sm-inline-block">${v.key}</span>
            </span>
            <span>${v.value}</span>
        </li>`);
    });
});
