jQuery.getJSON('https://personal-manik.firebaseio.com/projects.json', function(res){
    var newFilter=[];
    function tagsClass(obj) { var className=[]
        jQuery.each(obj,function(k,v){ className.push(v.key); })
        return className.toString().split(',').join(' ');
    }
    jQuery.each(res,function(i,data){
        jQuery.each(data.tags, function(key, value) { var exists = false;
            jQuery.each(newFilter, function(k, v) { if(value.key == v.key){ exists = true }; });
            if(exists == false && value.key != "") { newFilter.push(value); }
        });
        // end project filter
        jQuery('#dataProjects').append(`<a class="animated item ${tagsClass(data.tags)}" href="${data.link}">
            <img class="thumb" src="${data.thumb}" alt="${data.title}">
        </a>`);
        // end project area
    });
    jQuery('#dataProjects').lightGallery({thumbnail:false});
    // end lightGallery 
    jQuery.each(newFilter,function(i,tag){
        jQuery('#dataProjectFilters').append(`<button type="button" class="btn" data-filter=".${tag.key}">${tag.value}</button>`);
    });
});
jQuery.getJSON('https://personal-manik.firebaseio.com/contact.json', function(res){
    jQuery('#google-map,#mobile-map>iframe').attr('src',res.map_iframe_url);
    jQuery('#dataContactText').text(res.description);
    jQuery.each(res.address,function(k,v){
        jQuery('#dataContact').find('.list-group').append(`
        <li class="list-group-item d-flex">
            <span title="${v.label}" class="mr-auto">
                <i class="fa ${v.icon} mr-2"></i>
                <span class="d-none d-sm-inline-block">${v.label}</span>
            </span>
            <span><a href="${v.link}" terget="_black">${v.title}</a></span>
        </li>`);
    })
});